targets.expr:
max by (mountpoint) (
  round(
    100 - (
      node_filesystem_avail_bytes{instance="$instance", job="$node_exporter", fstype!~"tmpfs|fuse.*|squashfs|iso9660", mountpoint!~"/boot.*|/snap.*"}
      /
      node_filesystem_size_bytes{instance="$instance", job="$node_exporter", fstype!~"tmpfs|fuse.*|squashfs|iso9660", mountpoint!~"/boot.*|/snap.*"}
    ) * 100
  )
)
