targets.expr:
round(
  100 - 100 *
  (
    windows_logical_disk_free_bytes{job="$windows_exporter", volume!~"HarddiskVolume.*"}
      or windows_disk_free_bytes{job="$windows_exporter", volume!~"HarddiskVolume.*"}
  )
  /
  (
    windows_logical_disk_size_bytes{job="$windows_exporter", volume!~"HarddiskVolume.*"}
      or windows_disk_size_bytes{job="$windows_exporter", volume!~"HarddiskVolume.*"}
  )
)

(
  max by (instance) (
    windows_logical_disk_size_bytes{job="$windows_exporter", volume!~"HarddiskVolume.*", instance=~"^($windows_hdd_exception)$"}
      or windows_disk_size_bytes{job="$windows_exporter", volume!~"HarddiskVolume.*", instance=~"^($windows_hdd_exception)$"}
  ) * 0 + 50
)
or
max by (instance) (
  round(
    100 - (
      (windows_logical_disk_free_bytes{job="$windows_exporter", volume!~"HarddiskVolume.*", instance!~"^($windows_hdd_exception)$"}
        or windows_disk_free_bytes{job="$windows_exporter", volume!~"HarddiskVolume.*", instance!~"^($windows_hdd_exception)$"})
      /
      (windows_logical_disk_size_bytes{job="$windows_exporter", volume!~"HarddiskVolume.*", instance!~"^($windows_hdd_exception)$"}
        or windows_disk_size_bytes{job="$windows_exporter", volume!~"HarddiskVolume.*", instance!~"^($windows_hdd_exception)$"})
      * 100
    )
  )
)
